import { useState, useEffect } from 'react';
import { Star, MessageSquare, User } from 'lucide-react';

const ReviewList = ({ userId, type = 'received' }) => {
    const [reviews, setReviews] = useState([]);

    useEffect(() => {
        const savedReviews = localStorage.getItem('wibeReviews');
        if (savedReviews) {
            const allReviews = JSON.parse(savedReviews);

            // Filter reviews based on type
            const filtered = type === 'received'
                ? allReviews.filter(r => r.sellerId === userId)
                : allReviews.filter(r => r.reviewerId === userId);

            setReviews(filtered.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt)));
        }
    }, [userId, type]);

    const formatDate = (dateString) => {
        const date = new Date(dateString);
        return date.toLocaleDateString('uz-UZ', {
            year: 'numeric',
            month: 'short',
            day: 'numeric'
        });
    };

    const getAverageRating = () => {
        if (reviews.length === 0) return 0;
        const total = reviews.reduce((sum, r) => sum + r.rating, 0);
        return (total / reviews.length).toFixed(1);
    };

    if (reviews.length === 0) {
        return (
            <div className="text-center py-12">
                <MessageSquare className="w-16 h-16 text-gray-600 mx-auto mb-4" />
                <p className="text-gray-400">
                    {type === 'received'
                        ? 'Hali baholashlar yo\'q'
                        : 'Siz hali hech kimni baholamadingiz'
                    }
                </p>
            </div>
        );
    }

    return (
        <div className="space-y-6">
            {/* Summary */}
            <div className="flex items-center gap-6 p-4 bg-slate-50 rounded-xl">
                <div className="text-center">
                    <div className="text-3xl font-bold text-white">{getAverageRating()}</div>
                    <div className="flex items-center gap-1 mt-1">
                        {[1, 2, 3, 4, 5].map((star) => (
                            <Star
                                key={star}
                                className={`w-4 h-4 ${star <= Math.round(getAverageRating())
                                        ? 'text-yellow-400 fill-current'
                                        : 'text-gray-600'
                                    }`}
                            />
                        ))}
                    </div>
                </div>
                <div className="text-gray-400">
                    <span className="text-white font-medium">{reviews.length}</span> ta baholash
                </div>
            </div>

            {/* Reviews List */}
            <div className="space-y-4">
                {reviews.map((review) => (
                    <div
                        key={review.id}
                        className="p-4 bg-slate-50 rounded-xl border border-slate-200"
                    >
                        <div className="flex items-start gap-4">
                            {/* Avatar */}
                            <div className="w-10 h-10 bg-gradient-to-br from-blue-500 to-blue-600 rounded-full flex items-center justify-center text-sm font-bold text-white flex-shrink-0">
                                {review.reviewerName?.charAt(0) || 'U'}
                            </div>

                            {/* Content */}
                            <div className="flex-1 min-w-0">
                                <div className="flex items-center justify-between gap-4 mb-2">
                                    <div>
                                        <span className="font-medium text-white">
                                            {review.reviewerName}
                                        </span>
                                        <span className="text-sm text-gray-500 ml-2">
                                            {formatDate(review.createdAt)}
                                        </span>
                                    </div>
                                    <div className="flex items-center gap-1">
                                        {[1, 2, 3, 4, 5].map((star) => (
                                            <Star
                                                key={star}
                                                className={`w-4 h-4 ${star <= review.rating
                                                        ? 'text-yellow-400 fill-current'
                                                        : 'text-gray-600'
                                                    }`}
                                            />
                                        ))}
                                    </div>
                                </div>

                                {/* Account Info */}
                                {review.accountTitle && (
                                    <p className="text-sm text-gray-500 mb-2">
                                        Akkaunt: <span className="text-gray-400">{review.accountTitle}</span>
                                    </p>
                                )}

                                {/* Comment */}
                                {review.comment && (
                                    <p className="text-gray-300">{review.comment}</p>
                                )}
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default ReviewList;

import { useState } from 'react';
import { Star, X, Send } from 'lucide-react';
import { useAuth } from '../context/AuthContext';

const ReviewModal = ({ isOpen, onClose, seller, account, onSubmit }) => {
    const { user } = useAuth();
    const [rating, setRating] = useState(0);
    const [hoverRating, setHoverRating] = useState(0);
    const [comment, setComment] = useState('');
    const [isSubmitting, setIsSubmitting] = useState(false);

    if (!isOpen) return null;

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (rating === 0) return;

        setIsSubmitting(true);

        const review = {
            id: Date.now(),
            rating,
            comment,
            reviewerId: user?.id,
            reviewerName: user?.name || 'Anonim',
            sellerId: seller?.id,
            sellerName: seller?.name,
            accountId: account?.id,
            accountTitle: account?.title,
            createdAt: new Date().toISOString()
        };

        // Save to localStorage
        const savedReviews = localStorage.getItem('wibeReviews');
        const reviews = savedReviews ? JSON.parse(savedReviews) : [];
        reviews.push(review);
        localStorage.setItem('wibeReviews', JSON.stringify(reviews));

        // Update seller rating in localStorage
        updateSellerRating(seller?.id, rating);

        setTimeout(() => {
            setIsSubmitting(false);
            onSubmit?.(review);
            onClose();
            setRating(0);
            setComment('');
        }, 500);
    };

    const updateSellerRating = (sellerId, newRating) => {
        const savedRatings = localStorage.getItem('wibeSellerRatings');
        const ratings = savedRatings ? JSON.parse(savedRatings) : {};

        if (!ratings[sellerId]) {
            ratings[sellerId] = { total: 0, count: 0 };
        }

        ratings[sellerId].total += newRating;
        ratings[sellerId].count += 1;

        localStorage.setItem('wibeSellerRatings', JSON.stringify(ratings));
    };

    const ratingLabels = ['', 'Yomon', 'Qoniqarsiz', 'Yaxshi', 'Juda yaxshi', 'A\'lo'];

    return (
        <div className="fixed inset-0 z-50 flex items-center justify-center p-4">
            {/* Backdrop */}
            <div
                className="absolute inset-0 bg-black/70 backdrop-blur-sm"
                onClick={onClose}
            />

            {/* Modal */}
            <div className="relative w-full max-w-md bg-white rounded-2xl border border-slate-200 shadow-2xl overflow-hidden">
                {/* Header */}
                <div className="flex items-center justify-between p-6 border-b border-slate-200">
                    <h2 className="text-xl font-bold text-white">Baholash</h2>
                    <button
                        onClick={onClose}
                        className="p-2 text-gray-400 hover:text-white hover:bg-white/10 rounded-lg transition-colors"
                    >
                        <X className="w-5 h-5" />
                    </button>
                </div>

                {/* Content */}
                <form onSubmit={handleSubmit} className="p-6 space-y-6">
                    {/* Seller Info */}
                    <div className="flex items-center gap-4 p-4 bg-slate-50 rounded-xl">
                        <div className="w-12 h-12 bg-gradient-to-br from-blue-500 to-blue-600 rounded-full flex items-center justify-center text-lg font-bold text-white">
                            {seller?.name?.charAt(0) || 'S'}
                        </div>
                        <div>
                            <p className="text-white font-medium">{seller?.name || 'Sotuvchi'}</p>
                            <p className="text-sm text-gray-400">{account?.title || 'Akkaunt'}</p>
                        </div>
                    </div>

                    {/* Star Rating */}
                    <div className="text-center">
                        <p className="text-sm text-gray-400 mb-3">Bahoingizni bering</p>
                        <div className="flex justify-center gap-2 mb-2">
                            {[1, 2, 3, 4, 5].map((star) => (
                                <button
                                    key={star}
                                    type="button"
                                    onClick={() => setRating(star)}
                                    onMouseEnter={() => setHoverRating(star)}
                                    onMouseLeave={() => setHoverRating(0)}
                                    className="p-1 transition-transform hover:scale-110"
                                >
                                    <Star
                                        className={`w-10 h-10 transition-colors ${star <= (hoverRating || rating)
                                                ? 'text-yellow-400 fill-current'
                                                : 'text-gray-600'
                                            }`}
                                    />
                                </button>
                            ))}
                        </div>
                        <p className="text-sm font-medium text-yellow-400 h-5">
                            {ratingLabels[hoverRating || rating]}
                        </p>
                    </div>

                    {/* Comment */}
                    <div>
                        <label className="block text-sm font-medium text-gray-300 mb-2">
                            Izoh (ixtiyoriy)
