import { createContext, useContext, useState, useEffect } from 'react';
import { useAuth } from './AuthContext';

const ChatContext = createContext(null);

export const ChatProvider = ({ children }) => {
    const { user } = useAuth();
    const [conversations, setConversations] = useState(() => {
        // Lazy initialization - birinchi renderda localStorage dan o'qish
        if (typeof window !== 'undefined') {
            const currentUser = JSON.parse(localStorage.getItem('wibeUser') || 'null');
            if (currentUser) {
                const savedConversations = localStorage.getItem(`wibeChats_${currentUser.id}`);
                if (savedConversations) {
                    try {
                        return JSON.parse(savedConversations);
                    } catch {
                        return [];
                    }
                }
            }
        }
        return [];
    });
    const [activeChat, setActiveChat] = useState(null);
    const [isOpen, setIsOpen] = useState(false);

    // Sync conversations when user changes
    useEffect(() => {
        if (!user) {
            setConversations([]);
            setActiveChat(null);
        }
    }, [user]);

    // Save conversations when they change
    useEffect(() => {
        if (user && conversations.length > 0) {
            localStorage.setItem(`wibeChats_${user.id}`, JSON.stringify(conversations));
        }
    }, [conversations, user]);

    // Start or open a conversation
    const startConversation = (seller, account) => {
        if (!user) return null;

        // Check if conversation already exists
        const existingConv = conversations.find(
            c => c.sellerId === seller.id && c.accountId === account.id
        );

        if (existingConv) {
            setActiveChat(existingConv);
            setIsOpen(true);
            return existingConv;
        }

        // Create new conversation
        const newConversation = {
            id: Date.now(),
            sellerId: seller.id,
            sellerName: seller.name,
            sellerRating: seller.rating,
            accountId: account.id,
            accountTitle: account.title,
            accountImage: account.image,
            messages: [],
            createdAt: new Date().toISOString(),
            lastMessage: null
        };

        setConversations(prev => [newConversation, ...prev]);
        setActiveChat(newConversation);
        setIsOpen(true);
        return newConversation;
    };

    // Send a message
    const sendMessage = (conversationId, text) => {
        if (!user || !text.trim()) return;

        const newMessage = {
            id: Date.now(),
            senderId: user.id,
            senderName: user.name,
            text: text.trim(),
            timestamp: new Date().toISOString(),
            read: false
        };

        setConversations(prev => prev.map(conv => {
            if (conv.id === conversationId) {
                return {
                    ...conv,
                    messages: [...conv.messages, newMessage],
                    lastMessage: newMessage
                };
            }
            return conv;
        }));

        // Update active chat if it's the current one
        if (activeChat?.id === conversationId) {
            setActiveChat(prev => ({
                ...prev,
                messages: [...prev.messages, newMessage],
                lastMessage: newMessage
            }));
        }
    };

    // Mark messages as read
    const markAsRead = (conversationId) => {
        setConversations(prev => prev.map(conv => {
            if (conv.id === conversationId) {
                return {
                    ...conv,
                    messages: conv.messages.map(msg => ({ ...msg, read: true }))
                };
            }
            return conv;
        }));
    };

    // Get unread count
    const getUnreadCount = () => {
        return conversations.reduce((count, conv) => {
            const unread = conv.messages.filter(
                msg => !msg.read && msg.senderId !== user?.id
            ).length;
            return count + unread;
        }, 0);
    };

    // Close chat
    const closeChat = () => {
        setIsOpen(false);
        setActiveChat(null);
    };

    // Open chat
    const openChat = (conversation = null) => {
        if (conversation) {
            setActiveChat(conversation);
        }
        setIsOpen(true);
    };

    const value = {
        conversations,
        activeChat,
        isOpen,
        startConversation,
        sendMessage,
        markAsRead,
        getUnreadCount,
        closeChat,
        openChat,
        setActiveChat
    };

    return (
        <ChatContext.Provider value={value}>
            {children}
        </ChatContext.Provider>
    );
};

export const useChat = () => {
    const context = useContext(ChatContext);
    if (!context) {
        throw new Error('useChat must be used within a ChatProvider');
    }
    return context;
};

export default ChatContext;

import { createContext, useContext, useState, useEffect } from 'react';

const CoinContext = createContext(null);

export const CoinProvider = ({ children }) => {
    const [coinState, setCoinState] = useState(() => {
        const saved = localStorage.getItem('wibeCoinState');
        if (saved) {
            const parsed = JSON.parse(saved);
            // Check if it's a new month
            const currentMonth = new Date().getMonth();
            const savedMonth = parsed.lastResetMonth;
            if (currentMonth !== savedMonth) {
                return {
                    balance: parsed.balance,
                    monthlyTransactions: 0,
                    monthlyEarned: 0,
                    lastResetMonth: currentMonth,
                    history: parsed.history || []
                };
            }
            return parsed;
        }
        return {
            balance: 0,
            monthlyTransactions: 0,
            monthlyEarned: 0,
            lastResetMonth: new Date().getMonth(),
            history: []
        };
    });

    useEffect(() => {
        localStorage.setItem('wibeCoinState', JSON.stringify(coinState));
    }, [coinState]);

    // Add coins for selling/buying account (max 5 transactions = 100 coins per month)
    const addCoins = (amount, reason) => {
        setCoinState(prev => {
            if (prev.monthlyTransactions >= 5) {
                return prev; // Max 5 transactions per month
            }
            const newHistory = [...prev.history, {
                id: Date.now(),
                amount,
                reason,
                date: new Date().toISOString(),
                type: 'earned'
            }];
            return {
                ...prev,
                balance: prev.balance + amount,
                monthlyTransactions: prev.monthlyTransactions + 1,
                monthlyEarned: prev.monthlyEarned + amount,
                history: newHistory
            };
        });
    };

    // Spend coins (for premium)
    const spendCoins = (amount, reason) => {
        if (coinState.balance < amount) return false;
        setCoinState(prev => ({
            ...prev,
            balance: prev.balance - amount,
            history: [...prev.history, {
                id: Date.now(),
                amount: -amount,
                reason,
                date: new Date().toISOString(),
                type: 'spent'
            }]
        }));
        return true;
    };

    // Can earn more coins this month?
    const canEarnCoins = coinState.monthlyTransactions < 5;

    // Get discount based on subscription type
    const getVoucherDiscount = (subscriptionType) => {
        if (subscriptionType === 'premium') return 50;
        if (subscriptionType === 'pro') return 20;
        return 0;
    };

    const value = {
        balance: coinState.balance,
        monthlyEarned: coinState.monthlyEarned,
        monthlyTransactions: coinState.monthlyTransactions,
        history: coinState.history,
        addCoins,
        spendCoins,
        canEarnCoins,
        getVoucherDiscount,
        COINS_PER_TRANSACTION: 20,
        MAX_MONTHLY_TRANSACTIONS: 5,
        PREMIUM_COST_IN_COINS: 100
    };

    return (
        <CoinContext.Provider value={value}>
            {children}
        </CoinContext.Provider>
    );
};

export const useCoins = () => {
    const context = useContext(CoinContext);
    if (!context) {
        throw new Error('useCoins must be used within a CoinProvider');
    }
    return context;
};

export default CoinContext;

import { createContext, useContext, useState, useEffect } from 'react';

const NotificationContext = createContext();

export const useNotifications = () => useContext(NotificationContext);

export const NotificationProvider = ({ children }) => {
    const [notifications, setNotifications] = useState(() => {
        // Lazy initialization - birinchi renderda localStorage dan o'qish
        if (typeof window !== 'undefined') {
            const saved = localStorage.getItem('wibeNotifications');
            if (saved) {
                try {
                    return JSON.parse(saved);
