                message: 'Sizning "PUBG Level 50" akkauntingiz 500,000 so\'mga sotildi.',
                time: new Date(Date.now() - 1000 * 60 * 30).toISOString(),
                read: false,
                icon: 'ЁЯТ░'
            },
            {
                id: 2,
                type: 'message',
                title: 'Yangi xabar',
                message: 'GamerPro sizga xabar yubordi.',
                time: new Date(Date.now() - 1000 * 60 * 60 * 2).toISOString(),
                read: false,
                icon: 'ЁЯТм'
            },
            {
                id: 3,
                type: 'system',
                title: 'Xush kelibsiz!',
                message: 'WibeStore platformasiga xush kelibsiz. Premium obunani sinab ko\'ring!',
                time: new Date(Date.now() - 1000 * 60 * 60 * 24).toISOString(),
                read: true,
                icon: 'ЁЯОЙ'
            },
            {
                id: 4,
                type: 'security',
                title: 'Yangi kirish',
                message: 'Hisobingizga yangi qurilmadan kirildi.',
                time: new Date(Date.now() - 1000 * 60 * 60 * 48).toISOString(),
                read: true,
                icon: 'ЁЯФР'
            }
        ];
    });

    const [unreadCount, setUnreadCount] = useState(() => {
        // Lazy initialization for unread count
        if (typeof window !== 'undefined') {
            const saved = localStorage.getItem('wibeNotifications');
            if (saved) {
                try {
                    const parsed = JSON.parse(saved);
                    return parsed.filter(n => !n.read).length;
                } catch {
                    return 0;
                }
            }
        }
        return 2; // Demo notifications dagi o'qilmagan xabarlar soni
    });

    // Save to localStorage
    const saveNotifications = (newNotifications) => {
        localStorage.setItem('wibeNotifications', JSON.stringify(newNotifications));
    };

    const addNotification = (notification) => {
        const newNotification = {
            id: Date.now(),
            ...notification,
            time: new Date().toISOString(),
            read: false
        };
        const updated = [newNotification, ...notifications];
        setNotifications(updated);
        setUnreadCount(prev => prev + 1);
        saveNotifications(updated);
    };

    const markAsRead = (id) => {
        const updated = notifications.map(n =>
            n.id === id ? { ...n, read: true } : n
        );
        setNotifications(updated);
        setUnreadCount(updated.filter(n => !n.read).length);
        saveNotifications(updated);
    };

    const markAllAsRead = () => {
        const updated = notifications.map(n => ({ ...n, read: true }));
        setNotifications(updated);
        setUnreadCount(0);
        saveNotifications(updated);
    };

    const deleteNotification = (id) => {
        const updated = notifications.filter(n => n.id !== id);
        setNotifications(updated);
        setUnreadCount(updated.filter(n => !n.read).length);
        saveNotifications(updated);
    };

    const clearAll = () => {
        setNotifications([]);
        setUnreadCount(0);
        saveNotifications([]);
    };

    return (
        <NotificationContext.Provider value={{
            notifications,
            unreadCount,
            addNotification,
            markAsRead,
            markAllAsRead,
            deleteNotification,
            clearAll
        }}>
            {children}
        </NotificationContext.Provider>
    );
};

import { createContext, useContext, useState, useEffect } from 'react';

const ThemeContext = createContext(null);

export const ThemeProvider = ({ children }) => {
    const [theme, setTheme] = useState(() => {
        const savedTheme = localStorage.getItem('wibeTheme');
        return savedTheme || 'dark';
    });

    useEffect(() => {
        document.documentElement.setAttribute('data-theme', theme);
        localStorage.setItem('wibeTheme', theme);
    }, [theme]);

    const toggleTheme = () => {
        setTheme(prev => prev === 'dark' ? 'light' : 'dark');
    };

    const value = {
        theme,
        toggleTheme,
        isDark: theme === 'dark'
    };

    return (
        <ThemeContext.Provider value={value}>
            {children}
        </ThemeContext.Provider>
    );
};

export const useTheme = () => {
    const context = useContext(ThemeContext);
    if (!context) {
        throw new Error('useTheme must be used within a ThemeProvider');
    }
    return context;
};

export default ThemeContext;

