import { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { User, ShoppingBag, Tag, Heart, Star, Settings, Edit2, LogOut, Package, MessageSquare, Clock, CheckCircle, XCircle, Eye, Trash2, PlusCircle } from 'lucide-react';
import { useAuth } from '../context/AuthContext';
import AccountCard from '../components/AccountCard';
import ReviewList from '../components/ReviewList';
import { accounts, games, formatPrice } from '../data/mockData';

const ProfilePage = () => {
    const navigate = useNavigate();
    const { user, isAuthenticated, logout } = useAuth();
    const [activeTab, setActiveTab] = useState('purchases');
    const [likedAccounts, setLikedAccounts] = useState([]);
    const [purchases, setPurchases] = useState([]);
    const [sales, setSales] = useState([]);
    const [myListings, setMyListings] = useState([]);
    const [reviewCount, setReviewCount] = useState(0);
    const [averageRating, setAverageRating] = useState(5.0);

    // Redirect if not logged in
    useEffect(() => {
        if (!isAuthenticated) {
            navigate('/login');
        }
    }, [isAuthenticated, navigate]);

    // Load user data from localStorage
    useEffect(() => {
        if (user) {
            // Load liked accounts
            const savedLikes = localStorage.getItem(`wibeLikes_${user.id}`);
            if (savedLikes) {
                const likedIds = JSON.parse(savedLikes);
                setLikedAccounts(accounts.filter(acc => likedIds.includes(acc.id)));
            }

            // Load purchases
            const savedPurchases = localStorage.getItem(`wibePurchases_${user.id}`);
            if (savedPurchases) {
                const purchaseIds = JSON.parse(savedPurchases);
                setPurchases(accounts.filter(acc => purchaseIds.includes(acc.id)));
            } else {
                const demoPurchases = accounts.slice(0, 2);
                setPurchases(demoPurchases);
                localStorage.setItem(`wibePurchases_${user.id}`, JSON.stringify(demoPurchases.map(a => a.id)));
            }

            // Load sales
            const savedSales = localStorage.getItem(`wibeSales_${user.id}`);
            if (savedSales) {
                const saleIds = JSON.parse(savedSales);
                setSales(accounts.filter(acc => saleIds.includes(acc.id)));
            } else {
                setSales([]);
            }

            // Load reviews received
            const savedReviews = localStorage.getItem('wibeReviews');
            if (savedReviews) {
                const allReviews = JSON.parse(savedReviews);
                const userReviews = allReviews.filter(r => r.sellerId === user.id);
                setReviewCount(userReviews.length);
                if (userReviews.length > 0) {
                    const avg = userReviews.reduce((sum, r) => sum + r.rating, 0) / userReviews.length;
                    setAverageRating(avg.toFixed(1));
                }
            }

            // Load my listings (e'lonlar)
            const savedListings = localStorage.getItem('wibeListings');
            if (savedListings) {
                const allListings = JSON.parse(savedListings);
                const userListings = allListings.filter(l => l.sellerId === user.id);
                setMyListings(userListings);
            }
        }
    }, [user]);

    const handleLogout = () => {
        logout();
        navigate('/');
    };

    const tabs = [
        { id: 'mylistings', label: 'Mening mahsulotlarim', icon: Package, count: myListings.length },
        { id: 'purchases', label: 'Sotib olganlarim', icon: ShoppingBag, count: purchases.length },
        { id: 'sales', label: 'Sotganlarim', icon: Tag, count: sales.length },
        { id: 'likes', label: 'Yoqtirganlarim', icon: Heart, count: likedAccounts.length },
        { id: 'reviews', label: 'Baholashlarim', icon: MessageSquare, count: reviewCount },
    ];

    const getStatusBadge = (status) => {
        switch (status) {
            case 'pending':
                return { icon: Clock, text: 'Tekshirilmoqda', color: 'text-yellow-400 bg-yellow-400/10' };
            case 'approved':
                return { icon: CheckCircle, text: 'Tasdiqlangan', color: 'text-green-400 bg-green-400/10' };
            case 'rejected':
                return { icon: XCircle, text: 'Rad etilgan', color: 'text-red-400 bg-red-400/10' };
            case 'sold':
                return { icon: CheckCircle, text: 'Sotilgan', color: 'text-cyan-400 bg-cyan-400/10' };
            default:
                return { icon: Clock, text: 'Kutilmoqda', color: 'text-gray-400 bg-gray-400/10' };
        }
    };

    const getGameName = (gameId) => {
        const game = games.find(g => g.id === gameId);
        return game?.name || 'Noma\'lum';
    };

    const getGameImage = (gameId) => {
        const game = games.find(g => g.id === gameId);
        return game?.image || '';
    };

    const deleteListing = (listingId) => {
        if (window.confirm('Bu e\'lonni o\'chirishni xohlaysizmi?')) {
            const savedListings = JSON.parse(localStorage.getItem('wibeListings') || '[]');
            const updated = savedListings.filter(l => l.id !== listingId);
            localStorage.setItem('wibeListings', JSON.stringify(updated));
            setMyListings(myListings.filter(l => l.id !== listingId));
        }
    };

    if (!user) {
        return null;
    }

    return (
        <div className="min-h-screen" style={{ paddingTop: '140px', paddingBottom: '64px' }}>
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                {/* Profile Header */}
                <div className="bg-white rounded-3xl p-6 lg:p-8 border border-slate-200 mb-8 shadow-sm">
                    <div className="flex flex-col md:flex-row items-center gap-6">
                        {/* Avatar */}
                        <div className="relative">
                            <div className="w-24 h-24 bg-gradient-to-br from-blue-500 to-blue-600 rounded-full flex items-center justify-center text-4xl font-bold shadow-lg shadow-blue-500/30" style={{ color: '#ffffff' }}>
                                {user.name?.charAt(0) || 'U'}
                            </div>
                            <button className="absolute bottom-0 right-0 w-8 h-8 bg-blue-500 rounded-full flex items-center justify-center border-2 border-white hover:bg-blue-600 transition-colors">
                                <Edit2 className="w-4 h-4" style={{ color: '#ffffff' }} />
                            </button>
                        </div>

                        {/* User Info */}
                        <div className="flex-1 text-center md:text-left">
                            <h1 className="text-2xl lg:text-3xl font-bold text-gray-800 mb-2">{user.name}</h1>
                            <p className="text-gray-500 mb-3">{user.email}</p>
                            <div className="flex flex-wrap items-center justify-center md:justify-start gap-4 text-sm">
                                <div className="flex items-center gap-2 px-3 py-1.5 bg-slate-100 rounded-full">
                                    <Star className="w-4 h-4 text-yellow-400 fill-current" />
                                    <span className="text-gray-800">{averageRating}</span>
                                    <span className="text-gray-500">({reviewCount})</span>
                                </div>
                                <div className="flex items-center gap-2 px-3 py-1.5 bg-slate-100 rounded-full">
                                    <Package className="w-4 h-4 text-cyan-400" />
                                    <span className="text-gray-300">{sales.length} ta sotuvlar</span>
                                </div>
                                <div className="flex items-center gap-2 px-3 py-1.5 bg-slate-100 rounded-full">
                                    <ShoppingBag className="w-4 h-4 text-blue-500" />
                                    <span className="text-gray-300">{purchases.length} ta xaridlar</span>
                                </div>
                            </div>
                        </div>

                        {/* Actions */}
                        <div className="flex items-center gap-3">
                            <Link
                                to="/settings"
                                className="p-3 bg-slate-100 rounded-xl text-gray-400 hover:bg-white/10 hover:text-white transition-colors"
                            >
                                <Settings className="w-5 h-5" />
                            </Link>
                            <button
                                onClick={handleLogout}
                                className="flex items-center gap-2 px-4 py-3 bg-red-500/10 text-red-400 rounded-xl hover:bg-red-500/20 transition-colors"
                            >
                                <LogOut className="w-5 h-5" />
                                <span className="hidden sm:inline">Chiqish</span>
                            </button>
                        </div>
                    </div>
                </div>

                {/* Tabs */}
                <div className="flex flex-wrap gap-2 mb-8">
                    {tabs.map((tab) => (
                        <button
                            key={tab.id}
                            onClick={() => setActiveTab(tab.id)}
                            className={`flex items-center gap-2 px-5 py-3 rounded-xl font-medium transition-all ${activeTab === tab.id
                                ? 'bg-gradient-to-r from-blue-500 to-blue-600 shadow-lg shadow-blue-500/25'
                                : 'bg-white text-gray-600 border border-slate-200 hover:border-blue-300 hover:bg-blue-50'
                                }`}
                            style={activeTab === tab.id ? { color: '#ffffff' } : {}}
                        >
                            <tab.icon className="w-5 h-5" />
                            {tab.label}
                            <span className={`px-2 py-0.5 rounded-full text-xs ${activeTab === tab.id ? 'bg-white/20' : 'bg-white/10'
                                }`}>
                                {tab.count}
                            </span>
                        </button>
                    ))}
                </div>

                {/* Tab Content */}
                <div className="bg-white rounded-2xl p-6 border border-slate-200 min-h-[400px] shadow-sm">
                    {activeTab === 'mylistings' && (
                        <>
                            <div className="flex items-center justify-between mb-6">
                                <h2 className="text-xl font-bold text-gray-800">Mening mahsulotlarim</h2>
                                <Link
                                    to="/sell"
                                    className="flex items-center gap-2 px-4 py-2 bg-gradient-to-r from-green-500 to-emerald-500 rounded-xl text-white text-sm font-medium"
                                >
                                    <PlusCircle className="w-4 h-4" />
                                    Yangi e'lon
                                </Link>
                            </div>
                            {myListings.length > 0 ? (
                                <div className="space-y-4">
                                    {myListings.map((listing) => {
                                        const status = getStatusBadge(listing.status);
                                        const StatusIcon = status.icon;
                                        return (
                                            <div key={listing.id} className="flex flex-col sm:flex-row gap-4 p-4 bg-slate-50 rounded-xl border border-slate-200">
                                                {/* Image */}
                                                <div className="w-full sm:w-32 h-32 flex-shrink-0 rounded-xl overflow-hidden bg-slate-100">
                                                    {listing.images && listing.images.length > 0 ? (
                                                        <img
                                                            src={listing.images[0]}
                                                            alt={listing.title}
                                                            className="w-full h-full object-cover"
                                                        />
                                                    ) : (
                                                        <div className="w-full h-full flex items-center justify-center">
                                                            <img
                                                                src={getGameImage(listing.gameId)}
                                                                alt={getGameName(listing.gameId)}
                                                                className="w-16 h-16 object-cover rounded-lg opacity-50"
                                                            />
                                                        </div>
                                                    )}
                                                </div>

                                                {/* Info */}
                                                <div className="flex-1">
                                                    <div className="flex flex-wrap items-start justify-between gap-2 mb-2">
                                                        <div>
                                                            <h3 className="text-lg font-semibold text-gray-800">{listing.title}</h3>
                                                            <p className="text-sm text-gray-400">{getGameName(listing.gameId)}</p>
                                                        </div>
                                                        <div className={`flex items-center gap-1.5 px-3 py-1 rounded-full text-sm ${status.color}`}>
                                                            <StatusIcon className="w-4 h-4" />
                                                            {status.text}
                                                        </div>
                                                    </div>

                                                    <div className="flex flex-wrap gap-2 mb-3">
                                                        {listing.level && (
                                                            <span className="px-2 py-1 bg-slate-100 rounded text-xs text-gray-300">Level: {listing.level}</span>
                                                        )}
                                                        {listing.rank && (
                                                            <span className="px-2 py-1 bg-slate-100 rounded text-xs text-gray-300">Rank: {listing.rank}</span>
                                                        )}
                                                        {listing.skins && (
                                                            <span className="px-2 py-1 bg-slate-100 rounded text-xs text-gray-300">Skinlar: {listing.skins}</span>
                                                        )}
                                                    </div>

                                                    <div className="flex items-center justify-between">
                                                        <p className="text-xl font-bold bg-gradient-to-r from-blue-500 to-blue-600 bg-clip-text text-transparent">
                                                            {formatPrice(listing.price)} so'm
                                                        </p>
                                                        <div className="flex items-center gap-2">
                                                            <span className="text-xs text-gray-500">
                                                                {new Date(listing.createdAt).toLocaleDateString('uz-UZ')}
                                                            </span>
                                                            <button
                                                                onClick={() => deleteListing(listing.id)}
                                                                className="p-2 text-gray-400 hover:text-red-400 hover:bg-red-500/10 rounded-lg transition-colors"
                                                            >
                                                                <Trash2 className="w-4 h-4" />
                                                            </button>
                                                        </div>
                                                    </div>
                                                </div>

                                                {/* Images preview */}
                                                {listing.images && listing.images.length > 1 && (
                                                    <div className="flex sm:flex-col gap-2">
                                                        {listing.images.slice(1, 4).map((img, idx) => (
                                                            <div key={idx} className="w-12 h-12 rounded-lg overflow-hidden border border-white/10">
                                                                <img src={img} alt="" className="w-full h-full object-cover" />
                                                            </div>
                                                        ))}
                                                        {listing.images.length > 4 && (
                                                            <div className="w-12 h-12 rounded-lg bg-slate-100 flex items-center justify-center text-xs text-gray-400">
                                                                +{listing.images.length - 4}
                                                            </div>
                                                        )}
                                                    </div>
                                                )}
                                            </div>
                                        );
                                    })}
                                </div>
                            ) : (
                                <div className="text-center py-12">
                                    <Package className="w-16 h-16 text-gray-600 mx-auto mb-4" />
                                    <p className="text-gray-400 mb-4">Hali e'lon bermadingiz</p>
                                    <Link
                                        to="/sell"
                                        className="inline-flex items-center gap-2 px-6 py-3 bg-gradient-to-r from-blue-500 to-blue-600 rounded-xl text-white font-medium"
                                    >
                                        <PlusCircle className="w-5 h-5" />
                                        E'lon berish
                                    </Link>
                                </div>
                            )}
                        </>
                    )}

                    {activeTab === 'purchases' && (
                        <>
                            <h2 className="text-xl font-bold text-gray-800 mb-6">Sotib olgan akkauntlarim</h2>
                            {purchases.length > 0 ? (
                                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
                                    {purchases.map((account) => (
                                        <AccountCard key={account.id} account={account} />
                                    ))}
                                </div>
                            ) : (
                                <div className="text-center py-12">
                                    <ShoppingBag className="w-16 h-16 text-gray-600 mx-auto mb-4" />
                                    <p className="text-gray-400 mb-4">Hali hech narsa sotib olmadingiz</p>
                                    <Link
                                        to="/"
                                        className="inline-flex items-center gap-2 px-6 py-3 bg-gradient-to-r from-blue-500 to-blue-600 rounded-xl text-white font-medium"
                                    >
                                        Xarid qilish
                                    </Link>
                                </div>
                            )}
                        </>
                    )}

                    {activeTab === 'sales' && (
                        <>
                            <div className="flex items-center justify-between mb-6">
                                <h2 className="text-xl font-bold text-gray-800">Sotgan akkauntlarim</h2>
                                <Link
                                    to="/sell"
                                    className="flex items-center gap-2 px-4 py-2 bg-gradient-to-r from-blue-500 to-blue-600 rounded-xl text-white text-sm font-medium"
                                >
                                    <Tag className="w-4 h-4" />
                                    Yangi e'lon
                                </Link>
                            </div>
                            {sales.length > 0 ? (
                                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
                                    {sales.map((account) => (
                                        <AccountCard key={account.id} account={account} />
                                    ))}
                                </div>
                            ) : (
                                <div className="text-center py-12">
                                    <Tag className="w-16 h-16 text-gray-600 mx-auto mb-4" />
                                    <p className="text-gray-400 mb-4">Hali hech narsa sotmadingiz</p>
                                    <Link
                                        to="/sell"
                                        className="inline-flex items-center gap-2 px-6 py-3 bg-gradient-to-r from-blue-500 to-blue-600 rounded-xl text-white font-medium"
                                    >
                                        E'lon berish
                                    </Link>
                                </div>
                            )}
                        </>
                    )}

                    {activeTab === 'likes' && (
                        <>
                            <h2 className="text-xl font-bold text-gray-800 mb-6">Yoqtirgan akkauntlarim</h2>
                            {likedAccounts.length > 0 ? (
                                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
                                    {likedAccounts.map((account) => (
                                        <AccountCard key={account.id} account={account} />
                                    ))}
                                </div>
                            ) : (
                                <div className="text-center py-12">
                                    <Heart className="w-16 h-16 text-gray-600 mx-auto mb-4" />
                                    <p className="text-gray-400 mb-4">Hali yoqtirgan akkauntlar yo'q</p>
                                    <Link
                                        to="/"
                                        className="inline-flex items-center gap-2 px-6 py-3 bg-gradient-to-r from-blue-500 to-blue-600 rounded-xl text-white font-medium"
                                    >
                                        Akkauntlarni ko'rish
                                    </Link>
                                </div>
                            )}
                        </>
                    )}

                    {activeTab === 'reviews' && (
                        <>
                            <h2 className="text-xl font-bold text-gray-800 mb-6">Mening baholashlarim</h2>
                            <ReviewList userId={user.id} type="received" />
                        </>
                    )}
                </div>
            </div>
        </div>
    );
};

export default ProfilePage;

import { useState, useEffect } from 'react';
import { Search, Eye, EyeOff, Check, X, Crown, Ban, MoreVertical, Key, AlertCircle, RefreshCw } from 'lucide-react';
import { formatPrice, games } from '../../data/mockData';

const AdminAccounts = () => {
    const [selectedStatus, setSelectedStatus] = useState('all');
    const [searchQuery, setSearchQuery] = useState('');
    const [listings, setListings] = useState([]);
    const [selectedListing, setSelectedListing] = useState(null);
    const [showCredentials, setShowCredentials] = useState(false);
    const [showPassword, setShowPassword] = useState(false);
    const [message, setMessage] = useState({ type: '', text: '' });

    const loadListings = () => {
        const savedListings = localStorage.getItem('wibeListings');
        if (savedListings) {
            setListings(JSON.parse(savedListings));
        }
    };

    // Load listings from localStorage
    useEffect(() => {
        loadListings();
    }, []);

    const statusFilters = [
        { value: 'all', label: 'Barchasi' },
        { value: 'pending', label: 'Kutilmoqda' },
        { value: 'active', label: 'Faol' },
        { value: 'rejected', label: 'Rad etilgan' },
        { value: 'sold', label: 'Sotilgan' },
    ];

    const getStatusBadge = (status) => {
        const styles = {
            active: 'bg-green-500/20 text-green-400',
            pending: 'bg-yellow-500/20 text-yellow-400',
            rejected: 'bg-red-500/20 text-red-400',
            sold: 'bg-blue-500/20 text-blue-400'
        };
        const labels = {
            active: 'Faol',
            pending: 'Kutilmoqda',
            rejected: 'Rad etilgan',
            sold: 'Sotilgan'
        };
        return (
            <span className={`px-2.5 py-1 rounded-full text-xs font-medium ${styles[status] || 'bg-gray-500/20 text-gray-400'}`}>
                {labels[status] || status}
            </span>
        );
    };

    const getGameName = (gameId) => {
        const game = games.find(g => g.id === gameId);
        return game?.name || gameId;
    };

    const handleApprove = (listing) => {
        const updatedListings = listings.map(l => {
            if (l.id === listing.id) {
                return { ...l, status: 'active', approvedAt: new Date().toISOString() };
            }
            return l;
        });
        localStorage.setItem('wibeListings', JSON.stringify(updatedListings));
        setListings(updatedListings);
        setMessage({ type: 'success', text: `"${listing.title}" tasdiqlandi!` });
        setTimeout(() => setMessage({ type: '', text: '' }), 3000);
    };

    const handleReject = (listing) => {
        if (!window.confirm(`"${listing.title}" ni rad etmoqchimisiz?`)) return;

        const updatedListings = listings.map(l => {
            if (l.id === listing.id) {
                return { ...l, status: 'rejected', rejectedAt: new Date().toISOString() };
            }
            return l;
        });
        localStorage.setItem('wibeListings', JSON.stringify(updatedListings));
        setListings(updatedListings);
        setMessage({ type: 'success', text: `"${listing.title}" rad etildi!` });
        setTimeout(() => setMessage({ type: '', text: '' }), 3000);
    };

    const handleDelete = (listing) => {
        if (!window.confirm(`"${listing.title}" ni o'chirmoqchimisiz?`)) return;

        const updatedListings = listings.filter(l => l.id !== listing.id);
        localStorage.setItem('wibeListings', JSON.stringify(updatedListings));
        setListings(updatedListings);
        setMessage({ type: 'success', text: `E'lon o'chirildi!` });
        setTimeout(() => setMessage({ type: '', text: '' }), 3000);
    };

    const viewCredentials = (listing) => {
        setSelectedListing(listing);
        setShowCredentials(true);
        setShowPassword(false);
    };

    const filteredListings = listings.filter(listing => {
        const matchesStatus = selectedStatus === 'all' || listing.status === selectedStatus;
        const matchesSearch = listing.title?.toLowerCase().includes(searchQuery.toLowerCase()) ||
            listing.sellerName?.toLowerCase().includes(searchQuery.toLowerCase());
        return matchesStatus && matchesSearch;
    });

    const pendingCount = listings.filter(l => l.status === 'pending').length;

    return (
        <div className="space-y-6">
            <div className="flex flex-col sm:flex-row sm:items-center justify-between gap-4">
                <div>
                    <h1 className="text-2xl font-bold text-white mb-1">Akkauntlar</h1>
                    <p className="text-gray-400 text-sm">Foydalanuvchi e'lonlarini boshqaring</p>
                </div>
                <div className="flex items-center gap-2">
                    <button
                        onClick={loadListings}
                        className="p-2 bg-[#1e1e32] text-gray-400 hover:text-white rounded-lg transition-colors"
                        title="Yangilash"
                    >
                        <RefreshCw className="w-5 h-5" />
                    </button>
                    {pendingCount > 0 && (
                        <span className="px-3 py-1.5 bg-yellow-500/20 text-yellow-400 rounded-full text-sm font-medium">
                            {pendingCount} ta kutilmoqda
                        </span>
                    )}
                </div>
            </div>

            {/* Success/Error Message */}
            {message.text && (
                <div className={`p-4 rounded-xl flex items-center gap-3 ${message.type === 'success' ? 'bg-green-500/10 border border-green-500/30 text-green-400' : 'bg-red-500/10 border border-red-500/30 text-red-400'
                    }`}>
                    <Check className="w-5 h-5" />
                    {message.text}
                </div>
            )}

            {/* Filters */}
            <div className="flex flex-col lg:flex-row gap-4">
                <div className="relative flex-1">
                    <Search className="absolute left-4 top-1/2 -translate-y-1/2 w-5 h-5 text-gray-500" />
                    <input
                        type="text"
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                        placeholder="Akkaunt yoki sotuvchi qidirish..."
                        className="w-full pl-12 pr-4 py-3 bg-[#1e1e32] border border-white/10 rounded-xl text-white placeholder:text-gray-500 focus:outline-none focus:border-purple-500/50"
                    />
                </div>

                <div className="flex gap-2 overflow-x-auto pb-2 lg:pb-0">
                    {statusFilters.map((filter) => (
                        <button
                            key={filter.value}
                            onClick={() => setSelectedStatus(filter.value)}
                            className={`px-4 py-3 rounded-xl text-sm font-medium whitespace-nowrap transition-all ${selectedStatus === filter.value
                                ? 'bg-gradient-to-r from-purple-500 to-pink-500 text-white'
                                : 'bg-[#1e1e32] text-gray-300 hover:bg-[#25253a]'
                                }`}
                        >
                            {filter.label}
                        </button>
                    ))}
                </div>
            </div>

            {/* Table */}
            <div className="bg-[#1e1e32] rounded-2xl border border-white/5 overflow-hidden">
                <div className="overflow-x-auto">
                    <table className="w-full">
                        <thead>
                            <tr className="border-b border-white/5">
                                <th className="text-left text-xs text-gray-500 font-medium p-4">ID</th>
                                <th className="text-left text-xs text-gray-500 font-medium p-4">Akkaunt</th>
                                <th className="text-left text-xs text-gray-500 font-medium p-4">O'yin</th>
                                <th className="text-left text-xs text-gray-500 font-medium p-4">Sotuvchi</th>
                                <th className="text-left text-xs text-gray-500 font-medium p-4">Narx</th>
                                <th className="text-left text-xs text-gray-500 font-medium p-4">Status</th>
                                <th className="text-left text-xs text-gray-500 font-medium p-4">Sana</th>
                                <th className="text-left text-xs text-gray-500 font-medium p-4">Amallar</th>
                            </tr>
                        </thead>
                        <tbody>
                            {filteredListings.length > 0 ? (
                                filteredListings.map((listing) => (
                                    <tr key={listing.id} className="border-b border-white/5 last:border-0 hover:bg-white/[0.02]">
                                        <td className="p-4 text-gray-500 text-sm">#{listing.id}</td>
                                        <td className="p-4">
                                            <div className="font-medium text-white text-sm">{listing.title}</div>
                                            <div className="text-xs text-gray-500 mt-1">
                                                Level: {listing.level || '-'} | Rank: {listing.rank || '-'}
                                            </div>
                                        </td>
                                        <td className="p-4 text-gray-400 text-sm">{getGameName(listing.gameId)}</td>
                                        <td className="p-4">
                                            <span className="text-sm text-white">{listing.sellerName}</span>
                                        </td>
                                        <td className="p-4 text-cyan-400 text-sm font-medium">
                                            {formatPrice(Number(listing.price))}
                                        </td>
                                        <td className="p-4">
                                            {getStatusBadge(listing.status)}
                                        </td>
                                        <td className="p-4 text-gray-500 text-sm">
                                            {new Date(listing.createdAt).toLocaleDateString('uz-UZ')}
                                        </td>
                                        <td className="p-4">
                                            <div className="flex items-center gap-1">
                                                {/* View Credentials */}
                                                <button
                                                    onClick={() => viewCredentials(listing)}
                                                    className="p-2 text-purple-400 hover:bg-purple-500/20 rounded-lg transition-colors"
                                                    title="Login/Parol ko'rish"
                                                >
                                                    <Key className="w-4 h-4" />
                                                </button>

                                                {listing.status === 'pending' && (
                                                    <>
                                                        <button
                                                            onClick={() => handleApprove(listing)}
                                                            className="p-2 text-green-400 hover:bg-green-500/20 rounded-lg transition-colors"
                                                            title="Tasdiqlash"
                                                        >
                                                            <Check className="w-4 h-4" />
                                                        </button>
                                                        <button
                                                            onClick={() => handleReject(listing)}
                                                            className="p-2 text-red-400 hover:bg-red-500/20 rounded-lg transition-colors"
                                                            title="Rad etish"
                                                        >
                                                            <X className="w-4 h-4" />
                                                        </button>
                                                    </>
                                                )}
                                                {listing.status === 'active' && (
                                                    <button
                                                        onClick={() => handleReject(listing)}
                                                        className="p-2 text-red-400 hover:bg-red-500/20 rounded-lg transition-colors"
                                                        title="Bloklash"
                                                    >
                                                        <Ban className="w-4 h-4" />
                                                    </button>
                                                )}
                                                <button
                                                    onClick={() => handleDelete(listing)}
                                                    className="p-2 text-gray-400 hover:text-red-400 hover:bg-red-500/10 rounded-lg transition-colors"
                                                    title="O'chirish"
                                                >
                                                    <X className="w-4 h-4" />
                                                </button>
                                            </div>
                                        </td>
                                    </tr>
                                ))
                            ) : (
                                <tr>
                                    <td colSpan="8" className="p-12 text-center">
                                        <AlertCircle className="w-12 h-12 text-gray-600 mx-auto mb-4" />
                                        <p className="text-gray-400">Hech qanday e'lon topilmadi</p>
                                        <p className="text-gray-500 text-sm mt-1">Foydalanuvchilar e'lon qo'shganda bu yerda ko'rinadi</p>
                                    </td>
                                </tr>
                            )}
                        </tbody>
                    </table>
                </div>

                {/* Footer */}
                <div className="flex items-center justify-between p-4 border-t border-white/5">
                    <div className="text-sm text-gray-500">
                        {filteredListings.length} ta akkaunt topildi
                    </div>
                </div>
            </div>

            {/* Credentials Modal */}
            {showCredentials && selectedListing && (
                <div className="fixed inset-0 bg-black/70 backdrop-blur-sm z-50 flex items-center justify-center p-4">
                    <div className="bg-[#1e1e32] rounded-2xl p-6 w-full max-w-md border border-white/10">
                        <div className="flex items-center justify-between mb-6">
                            <div className="flex items-center gap-3">
                                <div className="w-12 h-12 bg-gradient-to-r from-purple-500 to-pink-500 rounded-full flex items-center justify-center">
                                    <Key className="w-6 h-6 text-white" />
                                </div>
                                <div>
                                    <h3 className="text-xl font-bold text-white">Akkaunt ma'lumotlari</h3>
                                    <p className="text-gray-400 text-sm">{selectedListing.title}</p>
                                </div>
                            </div>
                            <button
                                onClick={() => setShowCredentials(false)}
                                className="p-2 text-gray-400 hover:text-white hover:bg-white/10 rounded-lg"
                            >
                                <X className="w-5 h-5" />
                            </button>
                        </div>

                        <div className="space-y-4">
                            {/* Login Method */}
                            <div className="p-4 bg-[#25253a] rounded-xl">
                                <label className="text-xs text-gray-500 block mb-1">Kirish usuli</label>
                                <p className="text-white font-medium">{selectedListing.loginMethod || 'Email'}</p>
                            </div>

                            {/* Email/Login */}
                            <div className="p-4 bg-[#25253a] rounded-xl">
                                <label className="text-xs text-gray-500 block mb-1">Email / Login</label>
                                <div className="flex items-center justify-between">
                                    <p className="text-white font-medium font-mono">{selectedListing.accountEmail}</p>
                                    <button
                                        onClick={() => navigator.clipboard.writeText(selectedListing.accountEmail)}
                                        className="text-purple-400 text-sm hover:underline"
                                    >
                                        Nusxalash
                                    </button>
                                </div>
                            </div>

                            {/* Password */}
                            <div className="p-4 bg-[#25253a] rounded-xl">
                                <label className="text-xs text-gray-500 block mb-1">Parol</label>
                                <div className="flex items-center justify-between">
                                    <p className="text-white font-medium font-mono">
                                        {showPassword ? selectedListing.accountPassword : 'тАвтАвтАвтАвтАвтАвтАвтАвтАвтАв'}
                                    </p>
                                    <div className="flex items-center gap-2">
                                        <button
                                            onClick={() => setShowPassword(!showPassword)}
                                            className="text-gray-400 hover:text-white"
                                        >
                                            {showPassword ? <EyeOff className="w-4 h-4" /> : <Eye className="w-4 h-4" />}
                                        </button>
                                        <button
                                            onClick={() => navigator.clipboard.writeText(selectedListing.accountPassword)}
                                            className="text-purple-400 text-sm hover:underline"
                                        >
                                            Nusxalash
                                        </button>
                                    </div>
                                </div>
                            </div>

                            {/* Additional Info */}
                            {selectedListing.additionalInfo && (
                                <div className="p-4 bg-[#25253a] rounded-xl">
                                    <label className="text-xs text-gray-500 block mb-1">Qo'shimcha ma'lumot</label>
                                    <p className="text-white text-sm">{selectedListing.additionalInfo}</p>
                                </div>
                            )}

                            {/* Seller Info */}
                            <div className="p-4 bg-purple-500/10 border border-purple-500/30 rounded-xl">
                                <label className="text-xs text-purple-400 block mb-1">Sotuvchi</label>
                                <p className="text-white font-medium">{selectedListing.sellerName}</p>
                                <p className="text-gray-400 text-sm">ID: {selectedListing.sellerId}</p>
                            </div>
                        </div>

                        <div className="flex gap-3 mt-6">
                            {selectedListing.status === 'pending' && (
                                <>
                                    <button
                                        onClick={() => {
                                            handleApprove(selectedListing);
                                            setShowCredentials(false);
                                        }}
                                        className="flex-1 py-3 bg-green-500 text-white rounded-xl hover:bg-green-600 transition-colors flex items-center justify-center gap-2"
                                    >
                                        <Check className="w-5 h-5" />
                                        Tasdiqlash
                                    </button>
                                    <button
                                        onClick={() => {
                                            handleReject(selectedListing);
                                            setShowCredentials(false);
                                        }}
                                        className="flex-1 py-3 bg-red-500 text-white rounded-xl hover:bg-red-600 transition-colors flex items-center justify-center gap-2"
                                    >
                                        <X className="w-5 h-5" />
                                        Rad etish
                                    </button>
                                </>
                            )}
                            <button
                                onClick={() => setShowCredentials(false)}
                                className="flex-1 py-3 bg-white/5 text-gray-300 rounded-xl hover:bg-white/10 transition-colors"
                            >
                                Yopish
                            </button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default AdminAccounts;

import { useState, useEffect } from 'react';
import { Search, Crown, User, Check, X, Clock, Shield, Star } from 'lucide-react';

const AdminPremium = () => {
    const [searchQuery, setSearchQuery] = useState('');
    const [users, setUsers] = useState([]);
    const [selectedUser, setSelectedUser] = useState(null);
    const [showConfirmModal, setShowConfirmModal] = useState(false);
    const [premiumType, setPremiumType] = useState('premium');
    const [premiumDays, setPremiumDays] = useState(30);
    const [message, setMessage] = useState({ type: '', text: '' });

    const loadUsers = () => {
        const savedUsers = localStorage.getItem('wibeUsers');
        if (savedUsers) {
            setUsers(JSON.parse(savedUsers));
        } else {
            // Demo users
            const demoUsers = [
                { id: '1', name: 'Sardor', email: 'sardor@test.com', isPremium: false, premiumType: null, premiumExpiry: null },
                { id: '2', name: 'Jasur', email: 'jasur@test.com', isPremium: true, premiumType: 'premium', premiumExpiry: '2026-02-28' },
                { id: '3', name: 'Anvar', email: 'anvar@test.com', isPremium: true, premiumType: 'pro', premiumExpiry: '2026-03-15' },
            ];
            localStorage.setItem('wibeUsers', JSON.stringify(demoUsers));
            setUsers(demoUsers);
        }
    };

    // Load users from localStorage
    useEffect(() => {
        loadUsers();
    }, []);

    const filteredUsers = users.filter(user =>
        user.name?.toLowerCase().includes(searchQuery.toLowerCase()) ||
        user.email?.toLowerCase().includes(searchQuery.toLowerCase())
    );

    const handleGrantPremium = () => {
        if (!selectedUser) return;

        const expiryDate = new Date();
        expiryDate.setDate(expiryDate.getDate() + premiumDays);

        const updatedUsers = users.map(user => {
            if (user.id === selectedUser.id) {
                return {
                    ...user,
                    isPremium: true,
                    premiumType: premiumType,
                    premiumExpiry: expiryDate.toISOString().split('T')[0]
                };
            }
            return user;
        });

        localStorage.setItem('wibeUsers', JSON.stringify(updatedUsers));
        setUsers(updatedUsers);
        setShowConfirmModal(false);
        setSelectedUser(null);
        setMessage({ type: 'success', text: `${selectedUser.name} ga ${premiumType} berildi!` });

        setTimeout(() => setMessage({ type: '', text: '' }), 3000);
    };

    const handleRevokePremium = (user) => {
        if (!window.confirm(`${user.name} dan premiumni olib tashlamoqchimisiz?`)) return;

        const updatedUsers = users.map(u => {
            if (u.id === user.id) {
                return {
                    ...u,
                    isPremium: false,
                    premiumType: null,
                    premiumExpiry: null
                };
            }
            return u;
        });

        localStorage.setItem('wibeUsers', JSON.stringify(updatedUsers));
        setUsers(updatedUsers);
        setMessage({ type: 'success', text: `${user.name} dan premium olib tashlandi!` });

        setTimeout(() => setMessage({ type: '', text: '' }), 3000);
    };

    const getPremiumBadge = (user) => {
        if (!user.isPremium) return null;

        if (user.premiumType === 'pro') {
            return (
                <span className="flex items-center gap-1 px-2 py-1 bg-gradient-to-r from-cyan-500 to-blue-500 text-white text-xs font-medium rounded-full">
                    <Star className="w-3 h-3" />
                    Pro
                </span>
            );
        }
        return (
            <span className="flex items-center gap-1 px-2 py-1 bg-gradient-to-r from-yellow-500 to-orange-500 text-white text-xs font-medium rounded-full">
                <Crown className="w-3 h-3" />
                Premium
            </span>
        );
    };

    return (
        <div className="p-6">
            <div className="max-w-6xl mx-auto">
                {/* Header */}
                <div className="flex items-center justify-between mb-8">
                    <div>
                        <h1 className="text-2xl font-bold text-white flex items-center gap-3">
                            <Crown className="w-8 h-8 text-yellow-400" />
                            Premium boshqaruvi
                        </h1>
                        <p className="text-gray-400 mt-1">Foydalanuvchilarga premium tarif berish</p>
                    </div>
                </div>

                {/* Success/Error Message */}
                {message.text && (
                    <div className={`mb-6 p-4 rounded-xl flex items-center gap-3 ${message.type === 'success' ? 'bg-green-500/10 border border-green-500/30 text-green-400' : 'bg-red-500/10 border border-red-500/30 text-red-400'
                        }`}>
                        {message.type === 'success' ? <Check className="w-5 h-5" /> : <X className="w-5 h-5" />}
                        {message.text}
                    </div>
                )}

                {/* Search */}
                <div className="bg-[#1e1e32] rounded-2xl p-6 border border-white/5 mb-6">
                    <h2 className="text-lg font-semibold text-white mb-4">Foydalanuvchi qidirish</h2>
                    <div className="relative">
                        <Search className="absolute left-4 top-1/2 -translate-y-1/2 w-5 h-5 text-gray-500" />
                        <input
                            type="text"
                            value={searchQuery}
                            onChange={(e) => setSearchQuery(e.target.value)}
                            placeholder="Ism yoki email bo'yicha qidiring..."
                            className="w-full pl-12 pr-4 py-4 bg-[#25253a] border border-white/10 rounded-xl text-white placeholder:text-gray-500 focus:outline-none focus:border-purple-500/50"
                        />
                    </div>
                </div>

                {/* Users List */}
                <div className="bg-[#1e1e32] rounded-2xl border border-white/5 overflow-hidden">
                    <div className="p-4 border-b border-white/5">
                        <h2 className="text-lg font-semibold text-white">Foydalanuvchilar ({filteredUsers.length})</h2>
                    </div>

                    <div className="divide-y divide-white/5">
                        {filteredUsers.length > 0 ? (
                            filteredUsers.map((user) => (
                                <div key={user.id} className="p-4 flex items-center justify-between hover:bg-white/5 transition-colors">
                                    <div className="flex items-center gap-4">
                                        <div className="w-12 h-12 bg-gradient-to-br from-purple-500 to-pink-500 rounded-full flex items-center justify-center text-white font-bold">
                                            {user.name?.charAt(0) || 'U'}
                                        </div>
                                        <div>
                                            <div className="flex items-center gap-2">
                                                <span className="text-white font-medium">{user.name}</span>
                                                {getPremiumBadge(user)}
                                            </div>
                                            <p className="text-sm text-gray-400">{user.email}</p>
                                            {user.isPremium && user.premiumExpiry && (
                                                <p className="text-xs text-gray-500 flex items-center gap-1 mt-1">
                                                    <Clock className="w-3 h-3" />
                                                    Tugash: {user.premiumExpiry}
                                                </p>
                                            )}
                                        </div>
                                    </div>

                                    <div className="flex items-center gap-2">
                                        {user.isPremium ? (
                                            <button
                                                onClick={() => handleRevokePremium(user)}
                                                className="px-4 py-2 bg-red-500/10 text-red-400 rounded-xl hover:bg-red-500/20 transition-colors"
                                            >
                                                Bekor qilish
                                            </button>
                                        ) : (
                                            <button
                                                onClick={() => {
                                                    setSelectedUser(user);
                                                    setShowConfirmModal(true);
                                                }}
                                                className="px-4 py-2 bg-gradient-to-r from-yellow-500 to-orange-500 text-white rounded-xl hover:opacity-90 transition-opacity flex items-center gap-2"
                                            >
                                                <Crown className="w-4 h-4" />
                                                Premium berish
                                            </button>
                                        )}
                                    </div>
                                </div>
                            ))
                        ) : (
                            <div className="p-12 text-center">
                                <User className="w-16 h-16 text-gray-600 mx-auto mb-4" />
                                <p className="text-gray-400">Foydalanuvchi topilmadi</p>
                            </div>
                        )}
                    </div>
                </div>

                {/* Premium Stats */}
                <div className="grid grid-cols-1 sm:grid-cols-3 gap-4 mt-6">
                    <div className="bg-[#1e1e32] rounded-xl p-6 border border-white/5">
                        <div className="flex items-center gap-3 mb-2">
                            <User className="w-6 h-6 text-gray-400" />
                            <span className="text-gray-400">Jami foydalanuvchilar</span>
                        </div>
                        <p className="text-3xl font-bold text-white">{users.length}</p>
                    </div>
                    <div className="bg-[#1e1e32] rounded-xl p-6 border border-white/5">
                        <div className="flex items-center gap-3 mb-2">
                            <Crown className="w-6 h-6 text-yellow-400" />
                            <span className="text-gray-400">Premium</span>
                        </div>
                        <p className="text-3xl font-bold text-white">
                            {users.filter(u => u.isPremium && u.premiumType === 'premium').length}
                        </p>
                    </div>
                    <div className="bg-[#1e1e32] rounded-xl p-6 border border-white/5">
                        <div className="flex items-center gap-3 mb-2">
                            <Star className="w-6 h-6 text-cyan-400" />
                            <span className="text-gray-400">Pro</span>
                        </div>
                        <p className="text-3xl font-bold text-white">
                            {users.filter(u => u.isPremium && u.premiumType === 'pro').length}
                        </p>
                    </div>
                </div>
            </div>

            {/* Grant Premium Modal */}
            {showConfirmModal && selectedUser && (
                <div className="fixed inset-0 bg-black/70 backdrop-blur-sm z-50 flex items-center justify-center p-4">
                    <div className="bg-[#1e1e32] rounded-2xl p-6 w-full max-w-md border border-white/10">
                        <div className="flex items-center gap-3 mb-6">
                            <div className="w-12 h-12 bg-gradient-to-r from-yellow-500 to-orange-500 rounded-full flex items-center justify-center">
                                <Crown className="w-6 h-6 text-white" />
                            </div>
                            <div>
                                <h3 className="text-xl font-bold text-white">Premium berish</h3>
                                <p className="text-gray-400">{selectedUser.name}</p>
                            </div>
                        </div>

                        {/* Premium Type */}
                        <div className="mb-4">
                            <label className="block text-sm font-medium text-gray-300 mb-2">Tarif turi</label>
                            <div className="grid grid-cols-2 gap-3">
                                <button
                                    onClick={() => setPremiumType('premium')}
                                    className={`p-4 rounded-xl border transition-all flex flex-col items-center gap-2 ${premiumType === 'premium'
                                        ? 'bg-yellow-500/10 border-yellow-500/50 text-yellow-400'
                                        : 'bg-white/5 border-white/10 text-gray-400 hover:bg-white/10'
                                        }`}
                                >
                                    <Crown className="w-6 h-6" />
                                    Premium
                                </button>
                                <button
                                    onClick={() => setPremiumType('pro')}
                                    className={`p-4 rounded-xl border transition-all flex flex-col items-center gap-2 ${premiumType === 'pro'
                                        ? 'bg-cyan-500/10 border-cyan-500/50 text-cyan-400'
                                        : 'bg-white/5 border-white/10 text-gray-400 hover:bg-white/10'
                                        }`}
                                >
                                    <Star className="w-6 h-6" />
                                    Pro
                                </button>
                            </div>
                        </div>

                        {/* Duration */}
                        <div className="mb-6">
                            <label className="block text-sm font-medium text-gray-300 mb-2">Muddat (kun)</label>
                            <select
                                value={premiumDays}
                                onChange={(e) => setPremiumDays(Number(e.target.value))}
                                className="w-full px-4 py-3 bg-[#25253a] border border-white/10 rounded-xl text-white focus:outline-none focus:border-purple-500/50"
                            >
                                <option value={7}>7 kun</option>
                                <option value={30}>1 oy (30 kun)</option>
                                <option value={90}>3 oy (90 kun)</option>
                                <option value={180}>6 oy (180 kun)</option>
                                <option value={365}>1 yil (365 kun)</option>
                            </select>
                        </div>

                        {/* Actions */}
                        <div className="flex gap-3">
                            <button
                                onClick={() => {
                                    setShowConfirmModal(false);
                                    setSelectedUser(null);
                                }}
                                className="flex-1 py-3 bg-white/5 text-gray-300 rounded-xl hover:bg-white/10 transition-colors"
                            >
                                Bekor qilish
                            </button>
                            <button
                                onClick={handleGrantPremium}
                                className="flex-1 py-3 bg-gradient-to-r from-yellow-500 to-orange-500 text-white rounded-xl hover:opacity-90 transition-opacity flex items-center justify-center gap-2"
                            >
                                <Check className="w-5 h-5" />
                                Tasdiqlash
                            </button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default AdminPremium;
